mapper.select:
SELECT PRODUCT_ID ,PRODUCT_TITLE ,PRODUCT_DESCRIPTION ,PRODUCT_THUMNAIL ,PRODUCT_TYPE ,PRODUCT_GOOD ,PRODUCT_BAD ,PRODUCT_DATE ,PRODUCT_POINT FROM T_PRODUCT
AND PRODUCT_ID = #{productId}
AND PRODUCT_DESCRIPTION LIKE '%' || #{keyword} ||'%' OR PRODUCT_TITLE LIKE '%' || #{keyword} ||'%'
AND PRODUCT_TYPE = #{productType}
ORDER BY PRODUCT_GOOD
ORDER BY PRODUCT_DATE DESC

mapper.update:
UPDATE T_PRODUCT
   SET PRODUCT_TITLE = #{productTitle}
      ,PRODUCT_DESCRIPTION = #{productDescription}
      ,PRODUCT_THUMNAIL = #{productThumnail}
      ,PRODUCT_POINT = #{productPoint}
 WHERE PRODUCT_ID = #{productId}
