mapper.select:
SELECT PRODUCT_ID ,PRODUCT_TITLE ,PRODUCT_DESCRIPTION ,PRODUCT_THUMNAIL ,PRODUCT_TYPE ,PRODUCT_GOOD ,PRODUCT_BAD FROM T_PRODUCT
AND PRODUCT_ID = #{productId}
AND PRODUCT_DESCRIPTION LIKE '%' ||'파이썬' ||'%' OR PRODUCT_TITLE LIKE '%' ||'파이썬' ||'%'
AND PRODUCT_TYPE = #{productType}

mapper.update:
UPDATE T_PRODUCT
   SET PRODUCT_TITLE = #{productTitle}
 WHERE PRODUCT_ID = #{productId}
