mapper.update:
UPDATE T_QUESTION 
   SET QUEST_TITLE = #{questTitle}
	  ,QUEST_CONTENT = #{questContent}
	  ,QUEST_DATE = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
	WHERE QUEST_ID = #{questId}

mapper.select:
SELECT ANSWER_ID ,ANSWER_CONTENT ,ANSWER_SELECT ,ANSWER_DATE ,QUEST_ID ,USER_ID FROM T_ANSWER
AND ANSWER_ID = #{answerId}
AND QUEST_ID = #{questId}

mapper.update:
UPDATE T_ANSWER
   SET ANSWER_CONTENT = #{answerContent}
 WHERE ANSWER_ID = #{answerId}
